import React, { useState, useEffect } from 'react';

import './creador-pizza.styles.scss';

import { listaDeIngredientes, pizzaInicial } from './pizza.types';

const CreadorPizza: React.FC = () => {
	const [ingredientes, setIngredientes] = useState(listaDeIngredientes);
	const [pizza, setPizza] = useState(pizzaInicial);

	const agregarIngrediente = (nombreAgregar: string) => {
		const ingrediente = ingredientes.filter(
			({ nombre }) => nombre === nombreAgregar,
		);
		setIngredientes(ingredientes =>
			ingredientes.filter(({ nombre }) => nombre !== nombreAgregar),
		);
		setPizza(pizza => ({
			...pizza,
			ingredientes: [...pizza.ingredientes, ...ingrediente],
		}));
	};

	console.log(ingredientes.length);
	return (
		<div className='contenedor-creacion-pizza'>
			<h2>Crea tu Pizza</h2>
			<div className='listas-creacion'>
				<ul>
					<h3>Ingredientes</h3>
					{ingredientes.map(({ nombre, precio }) => (
						<li key={nombre} onClick={() => agregarIngrediente(nombre)}>
							{nombre} ${precio}
						</li>
					))}
				</ul>
				<ul>
					<h3>Pizza</h3>
					{pizza.ingredientes.map(({ nombre, precio }) => (
						<li key={nombre}>
							{nombre} ${precio}
						</li>
					))}
				</ul>
			</div>
		</div>
	);
};

export default CreadorPizza;
