export const capitalizarPalabras = (words: string) =>
	words
		.split(' ')
		.map(palabra => `${palabra[0].toUpperCase()}${palabra.slice(1)} `);

export const generarNombrePizza = (...nombreIngredientes: string[]) =>
	nombreIngredientes.reduce(
		(nombre, ingrediente) => nombre + ingrediente.slice(0, 2),
		'',
	);
