import React, { useState } from 'react';

import './creador-pizza.styles.scss';

import { listaDeIngredientes, Pizza } from './pizza.types';

const CreadorPizza: React.FC = () => {
	const [ingredientes, setIngredientes] = useState(listaDeIngredientes);
	const [pizza, setPizza] = useState({
		nombre: 'Pizza',
		ingredientes: [],
	} as Pizza);

	const agregarIngrediente = (nombre: string) => {
		setIngredientes(ingredientes =>
			ingredientes.filter(ingrediente => ingrediente.nombre !== nombre),
		);
	};

	console.log(ingredientes.length);
	return (
		<div className='contenedor-creacion-pizza'>
			<h2>Crea tu Pizza</h2>
			<div className='listas-creacion'>
				<ul>
					<h3>Ingredientes</h3>
					{ingredientes.map(({ nombre, precio }) => (
						<li key={nombre} onClick={() => agregarIngrediente(nombre)}>
							{nombre} ${precio}
						</li>
					))}
				</ul>
				<ul>
					<h3>Pizza</h3>
					{pizza.ingredientes.map(({ nombre, precio }) => (
						<li key={nombre}>
							{nombre} ${precio}
						</li>
					))}
				</ul>
			</div>
		</div>
	);
};

export default CreadorPizza;
