export const capitalizarPalabras = (words: string) => {
	const palabrasCapitalizadas = words.split(' ').map(palabra => {
		return `${palabra[0].toUpperCase()}${palabra.slice(0, 1)}`;
	});

	console.log({ palabrasCapitalizadas });
	return palabrasCapitalizadas;
};
