import { CreadorPizzaState } from './pizza.types';

export const ActionTypes = {
	AGREGAR_INGREDIENTE: 'AGREGAR_INGREDIENTE',
	REMOVER_INGREDIENTE: 'REMOVER_INGREDIENTE',
} as const;

export const agregarIngrediente = (nombre: string) => ({
	type: ActionTypes.AGREGAR_INGREDIENTE,
	payload: nombre,
});

export const removerIngrediente = (nombre: string) => ({
	type: ActionTypes.REMOVER_INGREDIENTE,
	payload: nombre,
});

type CreadorActions = ReturnType<typeof agregarIngrediente>;

export const creadorPizzaReducer = (
	state: CreadorPizzaState,
	action: CreadorActions,
) => {
	switch (action.type) {
		case ActionTypes.AGREGAR_INGREDIENTE:
			const { ingredientes, pizza } = state;
			const ingrediente = ingredientes.filter(
				({ nombre }) => nombre === action.payload,
			);
			return {
				...state,
				ingredientes: ingredientes.filter(
					({ nombre }) => nombre !== action.payload,
				),
				pizza: {
					...pizza,
					ingredientes: [...pizza.ingredientes, ...ingrediente],
				},
			} as CreadorPizzaState;
		default:
			return state;
	}
};
